import Header from "./components/Header";
import ProductList from "./components/ProductList";
import {useState} from "react";

import axios from "axios";

function App() {
    const [products, setProducts] = useState([
        {
            id: 1,
            title: "Египет",
            description: "Идеальное место для дайвинга и прогулок по дюнам пустыни",
            image: "https://avatars.mds.yandex.net/i?id=0c6cefecb3abcb2a63d41a8f43751cb0-5496696-images-thumbs&n=13&exp=1",
            price: 46250,
            favorite: false
        },
        {
            id: 2,
            title: "Мексика",
            description: "Земля ацтеков и майя с древней культурой и пляжной идиллией",
            image: "https://avatars.mds.yandex.net/i?id=72fff16b94bbee6b5793e7a632518669-5855963-images-thumbs&n=13&exp=1",
            price: 132000,
            favorite: false
        },
        {
            id: 3,
            title: "Мальдивы",
            description: "Райские острова с подводными отелями и ресторанами",
            image: "https://avatars.mds.yandex.net/i?id=4d9cc6cf0be8372f08c3fc9606bde842-5875471-images-thumbs&n=13&exp=1",
            price: 128950,
            favorite: false
        },
        {
            id: 4,
            title: "Индонезия",
            description: "Тысячи островов для дайвинга, серфинга и райского наслаждения",
            image: "https://avatars.mds.yandex.net/i?id=578df9b7146f19a1b292a0d6a507a3c7-5876724-images-thumbs&n=13&exp=1",
            price: 78964,
            favorite: false
        },
        {
            id: 5,
            title: "Test 5",
            description: "",
            image: "https://avatars.mds.yandex.net/i?id=2838c6fa7af15c84d29b83f2ea1cf53c-5655541-images-thumbs&n=13&exp=1",
            price: 20,
            favorite: false
        }
    ]);

    const favoriteProduct = (product) => {
        const isFavorite = product.favorite

        setProducts(
            products.map((item) =>
                item.id === product.id
                    ? {...item, favorite: true}
                    : item
            )
        )
    }

    const removeProduct = (product) => {
        setProducts(products.filter(p => p.id !== product.id))
    }

    return (
        <div className="App">
            <Header/>

            <ProductList remove={removeProduct} favorite={favoriteProduct} products={products}/>
        </div>
    );
}

export default App;
