import React from "react";
import {FaHeart, FaRegHeart, FaRegTrashAlt} from "react-icons/fa";
import {BsHeart, BsHeartFill} from "react-icons/bs";

import MyButton from "./UI/button/MyButton";

const ProductItem = ({product, favorite, remove}) => {
    // let favorite = '';
    // if(product.favorite) {
    //     favorite = <FaHeart/>;
    // } else {
    //     favorite = <FaRegHeart/>;
    // }

    return (
        <div className="card__item">
            <MyButton className="card__like" onClick={() => favorite(product)}>
                {product.favorite
                    ? <BsHeartFill/>
                    : <BsHeart/>
                }
            </MyButton>
            {/*<button className="card__like">*/}
            {/*    {favorite}*/}
            {/*</button>*/}

            <MyButton className="card__del" onClick={() => remove(product)}>
                <FaRegTrashAlt/>
            </MyButton>

            {/*<button className="card__del" >*/}
            {/*    <FaRegTrashAlt/>*/}
            {/*</button>*/}

            <div className="card__item-image">
                <img src={product.image} alt={product.title}/>
            </div>

            <div className="card__item-content">
                <div className="card__item-title">{product.title}</div>
                <div className="card__item-price">$ {product.price}</div>
            </div>
        </div>
    );
};

export default ProductItem;
