import Header from "./components/Header";
import ProductList from "./components/ProductList";
import {useState} from "react";
// import axios from "axios";

function App() {
	const [products, setProducts] = useState([
		{
			id: 1,
			title: "Test",
			image: "https://avatars.mds.yandex.net/i?id=79bdde17338230230564c06b205800e0-5879907-images-thumbs&n=13&exp=1",
			price: 50,
			favorite: false
		},
		{
			id: 2,
			title: "Test 2",
			image: "https://avatars.mds.yandex.net/i?id=2838c6fa7af15c84d29b83f2ea1cf53c-5655541-images-thumbs&n=13&exp=1",
			price: 20,
			favorite: true
		},
		{
			id: 3,
			title: "Test 3",
			image: "https://avatars.mds.yandex.net/i?id=2838c6fa7af15c84d29b83f2ea1cf53c-5655541-images-thumbs&n=13&exp=1",
			price: 20,
			favorite: true
		},
		{
			id: 4,
			title: "Test 4",
			image: "https://avatars.mds.yandex.net/i?id=2838c6fa7af15c84d29b83f2ea1cf53c-5655541-images-thumbs&n=13&exp=1",
			price: 20,
			favorite: true
		},
		{
			id: 5,
			title: "Test 5",
			image: "https://avatars.mds.yandex.net/i?id=2838c6fa7af15c84d29b83f2ea1cf53c-5655541-images-thumbs&n=13&exp=1",
			price: 20,
			favorite: true
		}
	]);

	// const fetchProducts = async () => {
	// 	const response = await axios.get("https://fakestoreapi.com/products").catch((err) => {
	// 		console.log("Err", err);
	// 	});
	//
	// 	response.data.forEach((product) => {
	// 		const { id, title, image, price } = product;
	// 		products.push({ id, title, image, price, favorite: false });
	// 	});
	// };

	const favoriteProduct = (product) => {
		if(product.favorite) {
			setProducts(products.product.favorite = false);
		} else {
			product.favorite = true;
		}
		// setProducts(products.find(p => p.id !== product.id))
		//
		// if(product.favorite) {
		// 	setProducts(products.product.favorite = false);
		// } else {
		// 	setProducts(products.product.favorite = true);
		// }
	}

	const removeProduct = (product) => {
		setProducts(products.filter(p => p.id !== product.id))
	}

	return (
		<div className="App">
			<Header />

			<ProductList remove={removeProduct} favorite={favoriteProduct} products={products}/>
		</div>
	);
}

export default App;
