        return (
            <h1>
                Нет товаров!
            </h1>
        )
    }

    return (
        <div className="container">
            {loading ? (<h1>Идёт загрузка...</h1>) :
                <h1>Загрузился</h1>
            }

            {/*{products.map((product) =>*/}
            {/*    <ProductItem favorite={favorite} remove={remove} product={product} key={product.id}/>*/}
            {/*)}*/}
        </div>
    );
};

export default ProductList;
